import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import { Box, TextField, Button, Typography, Card, CardMedia } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        background: {
            default: '#282c34',
        },
    },
    components: {
        MuiOutlinedInput: {
            styleOverrides: {
                root: {
                    '& .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    color: 'white',
                },
                input: {
                    color: 'white',
                },
                notchedOutline: {
                    borderColor: 'white',
                },
            },
        },
        MuiInputLabel: {
            styleOverrides: {
                root: {
                    color: 'white',
                },
            },
        },
    },
});

const UpdatePerson = () => {
    const { personId } = useParams();
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        description: '',
        imageLink: '',
    });
    const [imagePreview, setImagePreview] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPerson = async () => {
            try {
                const response = await axios.post('https://peoplemuseumyeah.uc.r.appspot.com/db/getPerson', {
                    personId,
                }, {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                if (response.data && response.data.data) {
                    const person = response.data.data;
                    setFormData({
                        firstName: person.name.split(' ')[0],
                        lastName: person.name.split(' ')[1],
                        description: person.description,
                        imageLink: person.imageLink,
                    });
                    setImagePreview(person.imageLink);
                } else {
                    console.log('No data returned');
                }
            } catch (error) {
                console.error('Error fetching person:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchPerson();
    }, [personId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });

        if (name === 'imageLink') {
            setImagePreview(value);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.put('https://peoplemuseumyeah.uc.r.appspot.com/db/updatePerson', {
                personId,
                name: `${formData.firstName} ${formData.lastName}`,
                imageLink: formData.imageLink,
                description: formData.description,
            }, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            console.log('Person updated successfully:', response.data);

            // Redirect to another page, for example, back to the list
            // You can use Link for declarative navigation in other parts of the app
        } catch (error) {
            console.error('Error updating person:', error);
        }
    };

    if (loading) {
        return <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</Box>;
    }

    if (error) {
        return <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Error: {error}</Box>;
    }

    return (
        <ThemeProvider theme={theme}>
            <Box
                component="form"
                onSubmit={handleSubmit}
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    minHeight: '100vh',
                    p: 2,
                }}
            >
                <Typography variant="h4" gutterBottom color="white">
                    Update Person
                </Typography>
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="First Name"
                    variant="outlined"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Last Name"
                    variant="outlined"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Description"
                    variant="outlined"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    multiline
                    rows={4}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Image URL"
                    variant="outlined"
                    name="imageLink"
                    value={formData.imageLink}
                    onChange={handleChange}
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                {imagePreview && (
                    <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
                        <CardMedia
                            component="img"
                            height="auto"
                            image={imagePreview}
                            alt="Image Preview"
                            sx={{ objectFit: 'contain' }}
                        />
                    </Card>
                )}
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    sx={{ marginTop: 2, width: '300px' }}
                >
                    Update
                </Button>
                {/* Example of using Link for navigation */}
                <Link to="/">Back to List</Link>
            </Box>
        </ThemeProvider>
    );
};

export default UpdatePerson;
