import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import { TextField, Button, Box, Typography, Card, CardMedia } from '@mui/material';

const UpdatePerson = () => {
    const { personId } = useParams(); // Assuming personId is obtained from the route params
    const [person, setPerson] = useState(null);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        description: '',
        imageURL: '',
    });
    const [imagePreview, setImagePreview] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchPerson = async () => {
            try {
                setLoading(true);
                const response = await axios.post(
                    'https://peoplemuseumyeah.uc.r.appspot.com/db/getPerson',
                    { personId },
                    {
                        headers: {
                            'Content-Type': 'application/json',
                        },
                    }
                );
                if (response.data && response.data.person) {
                    const { firstName, lastName, description, imageLink } = response.data.person;
                    setPerson(response.data.person);
                    setFormData({ firstName, lastName, description, imageURL: imageLink });
                    setImagePreview(imageLink); // Set initial image preview
                } else {
                    setError('Person not found');
                }
            } catch (error) {
                console.error('Error fetching person:', error);
                setError('Failed to fetch person data');
            } finally {
                setLoading(false);
            }
        };

        fetchPerson();
    }, [personId]);

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
        reader.onloadend = () => {
            setFormData({ ...formData, imageURL: reader.result });
            setImagePreview(reader.result);
        };
        if (file) {
            reader.readAsDataURL(file);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const { firstName, lastName, description, imageURL } = formData;
            const response = await axios.put(
                'https://peoplemuseumyeah.uc.r.appspot.com/db/updatePerson',
                {
                    personId,
                    newName: `${firstName} ${lastName}`,
                    newImageLink: imageURL,
                    newDescription: description,
                    newContext: "Example context",
                    newPublic: true,
                },
                {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                }
            );
            if (response.status === 200) {
                console.log('Person updated successfully:', response.data);
                // Redirect to person list or show success message
            } else {
                setError('Failed to update person');
            }
        } catch (error) {
            console.error('Error updating person:', error);
            setError('Failed to update person');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <Box>Loading...</Box>;
    }

    if (error) {
        return <Box>Error: {error}</Box>;
    }

    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <Typography variant="h4" gutterBottom>
                Update Person
            </Typography>
            {person && (
                <Box
                    component="form"
                    onSubmit={handleSubmit}
                    sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}
                >
                    <TextField
                        label="First Name"
                        variant="outlined"
                        value={formData.firstName}
                        onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                        required
                        sx={{ marginBottom: 2, width: '300px' }}
                    />
                    <TextField
                        label="Last Name"
                        variant="outlined"
                        value={formData.lastName}
                        onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                        required
                        sx={{ marginBottom: 2, width: '300px' }}
                    />
                    <TextField
                        label="Description"
                        variant="outlined"
                        value={formData.description}
                        onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                        multiline
                        rows={4}
                        required
                        sx={{ marginBottom: 2, width: '300px' }}
                    />
                    <Box sx={{ marginBottom: 2, width: '300px' }}>
                        <TextField
                            fullWidth
                            label="Image URL"
                            variant="outlined"
                            value={formData.imageURL}
                            onChange={(e) => setFormData({ ...formData, imageURL: e.target.value })}
                        />
                        <Button
                            variant="contained"
                            component="label"
                            sx={{ marginTop: 1 }}
                        >
                            Upload Image
                            <input
                                type="file"
                                name="pic"
                                hidden
                                onChange={handleFileChange}
                            />
                        </Button>
                    </Box>
                    {imagePreview && (
                        <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
                            <CardMedia
                                component="img"
                                height="auto"
                                image={imagePreview}
                                alt="Image Preview"
                                sx={{ objectFit: 'contain' }}
                            />
                        </Card>
                    )}
                    <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                        sx={{ marginTop: 2, width: '300px' }}
                    >
                        Update
                    </Button>
                </Box>
            )}
            <Button component={Link} to="/person-list" variant="outlined" sx={{ marginTop: 2 }}>
                Back to Person List
            </Button>
        </Box>
    );
};

export default UpdatePerson;
