import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom'; // Import Link and useParams from react-router-dom
import axios from 'axios';
import { Box, Typography, TextField, Button, Grid } from '@mui/material';

const UpdateCollection = () => {
    const { collectionId } = useParams(); // Get collectionId from URL params
    const [collection, setCollection] = useState({
        name: '',
        description: '',
        imageLink: '',
        isPublic: false,
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCollection = async () => {
            try {
                const response = await axios.get(`https://peoplemuseumyeah.uc.r.appspot.com/db/getCollectionById/${collectionId}`);

                if (response && response.data) {
                    setCollection(response.data);
                } else {
                    console.log('No data returned');
                }
            } catch (error) {
                console.error('Error fetching collection:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchCollection();
    }, [collectionId]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCollection({ ...collection, [name]: value });
    };

    const handleUpdate = async () => {
        try {
            const response = await axios.put(`https://peoplemuseumyeah.uc.r.appspot.com/db/updateCollection`, {
                collectionId,
                newName: collection.name,
                newDescription: collection.description,
                newImageLink: collection.imageLink,
                newIsPublic: collection.isPublic,
            });

            console.log('Update Status:', response.status);
            console.log('Update Data:', response.data);
            // Optionally handle success message or redirection
        } catch (error) {
            console.error('Error updating collection:', error);
            // Optionally handle error state or notification to user
        }
    };

    if (loading) {
        return <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</Box>;
    }

    if (error) {
        return <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Error: {error}</Box>;
    }

    return (
        <Box
            sx={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                minHeight: '100vh',
                p: 2,
            }}
        >
            <Typography variant="h4" gutterBottom color="primary" sx={{ textAlign: 'center' }}>
                Update Collection
            </Typography>
            <Grid container spacing={2} justifyContent="center">
                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        label="Name"
                        name="name"
                        value={collection.name}
                        onChange={handleInputChange}
                        sx={{ marginBottom: 2 }}
                    />
                    <TextField
                        fullWidth
                        label="Description"
                        name="description"
                        value={collection.description}
                        onChange={handleInputChange}
                        sx={{ marginBottom: 2 }}
                    />
                    <TextField
                        fullWidth
                        label="Image Link"
                        name="imageLink"
                        value={collection.imageLink}
                        onChange={handleInputChange}
                        sx={{ marginBottom: 2 }}
                    />
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleUpdate}
                        sx={{ marginRight: 2 }}
                    >
                        Update Collection
                    </Button>
                    <Button
                        component={Link}
                        to="/collections"
                        variant="outlined"
                        color="secondary"
                    >
                        Cancel
                    </Button>
                </Grid>
            </Grid>
        </Box>
    );
};

export default UpdateCollection;
