import React from 'react';
import { Link } from 'react-router-dom';
import { Button, Grid, Typography, makeStyles } from '@mui/material';
import './HomePage.css';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(2),
  },
  title: {
    marginBottom: theme.spacing(2),
  },
  photo: {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: theme.shape.borderRadius,
  },
  button: {
    margin: theme.spacing(1),
  },
}));

const HomePage = () => {
  const classes = useStyles();

  // 照片URL数组
  const people = [
    { id: 1, name: 'Person 1', photo:'https://images.unsplash.com/photo-1599566150163-29194dcaad36?crop=faces&fit=crop&w=200&h=200', description: 'Description of Person 1' },
    { id: 2, name: 'Person 2', photo:'https://images.unsplash.com/photo-1524504388940-b1c1722653e1?crop=faces&fit=crop&w=200&h=200', description: 'Description of Person 2' },
    { id: 2, name: 'Person 2', photo:'https://images.unsplash.com/photo-1494790108377-be9c29b29330?crop=faces&fit=crop&w=200&h=200', description: 'Description of Person 3' }
    // 可以添加更多照片URL
  ];

  return (
    <div className={classes.root}>
      <Typography variant="h1" className={classes.title}>People Museum</Typography>
      <Grid container spacing={2}>
        {people.map(person => (
          <Grid item xs={6} sm={4} md={3} key={person.id}>
            <Link to={`/person/${person.id}`}>
              <img src={person.photo} alt={person.name} className={classes.photo} />
            </Link>
          </Grid>
        ))}
      </Grid>
      <div>
        <Link to="/add-persona">
          <Button variant="contained" color="primary" className={classes.button}>Add Persona</Button>
        </Link>
        <Link to="/add-collection">
          <Button variant="contained" color="primary" className={classes.button}>Add Collection</Button>
        </Link>
      </div>
    </div>
  );
}

export default HomePage;
