import React, { useState } from 'react';
import { TextField, Button, Box, Typography, Card, CardMedia, CircularProgress } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    background: {
      default: '#282c34',
    },
  },
  components: {
    MuiOutlinedInput: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          '&:hover .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          color: 'white',
        },
        input: {
          color: 'white',
        },
        notchedOutline: {
          borderColor: 'white',
        },
      },
    },
    MuiInputLabel: {
      styleOverrides: {
        root: {
          color: 'white',
        },
      },
    },
  },
});

const AddPersona = ({ onAddPerson }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    description: '',
    pic: null,
    picURL: '',
    userId: 1,
    collectionId: 'collection1',
  });
  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFormData({ ...formData, pic: file, picURL: '' });
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleURLChange = (e) => {
    const { value } = e.target;
    setFormData({ ...formData, picURL: value, pic: null });
    setImagePreview(value);
  };

  const uploadToFileIO = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    const response = await fetch('https://file.io', {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      throw new Error('Failed to upload image');
    }

    const data = await response.json();
    console.log('Uploaded image data:', data); // Log the response data
    return data.link;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      let imageLink = formData.picURL;

      if (formData.pic) {
        imageLink = await uploadToFileIO(formData.pic);
        console.log('Image uploaded to file.io with link:', imageLink); // Log the image link
      }

      const formDataToSend = {
        name: `${formData.firstName} ${formData.lastName}`,
        imageLink,
        description: formData.description,
        context: 'Example context',
        userId: formData.userId,
        collectionId: formData.collectionId,
        public: true,
      };

      console.log('Form data to send:', formDataToSend); // Log the form data

      const response = await fetch('https://peoplemuseumyeah.uc.r.appspot.com/db/addPerson', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formDataToSend),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const responseData = await response.json();
      console.log('Person added successfully:', responseData);

      setFormData({
        firstName: '',
        lastName: '',
        description: '',
        pic: null,
        picURL: '',
        userId: 1,
        collectionId: 'collection1',
      });
      setImagePreview(null);
      onAddPerson(responseData);
    } catch (error) {
      console.error('Error adding person:', error);
      setError('Failed to add person. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Box
        component="form"
        onSubmit={handleSubmit}
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
          p: 2,
        }}
      >
        <Typography variant="h4" gutterBottom color="white">
          Add Person to Museum
        </Typography>
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="First Name"
          variant="outlined"
          name="firstName"
          value={formData.firstName}
          onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
          required
        />
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Last Name"
          variant="outlined"
          name="lastName"
          value={formData.lastName}
          onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
          required
        />
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Description"
          variant="outlined"
          name="description"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          multiline
          rows={4}
          required
        />
        <Box sx={{ marginBottom: 2, width: '300px' }}>
          <TextField
            fullWidth
            label="Image URL"
            variant="outlined"
            name="picURL"
            value={formData.picURL}
            onChange={handleURLChange}
          />
          <Button
            variant="contained"
            component="label"
            sx={{ marginTop: 1 }}
          >
            Upload Image
            <input
              type="file"
              name="pic"
              hidden
              onChange={handleFileChange}
            />
          </Button>
        </Box>
        {imagePreview && (
          <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
            <CardMedia
              component="img"
              height="auto"
              image={imagePreview}
              alt="Uploaded Image Preview"
              sx={{ objectFit: 'contain' }}
            />
          </Card>
        )}
        {loading && <CircularProgress sx={{ marginBottom: 2 }} />}
        {error && (
          <Typography color="error" sx={{ marginBottom: 2 }}>
            {error}
          </Typography>
        )}
        <Button
          type="submit"
          variant="contained"
          color="primary"
          sx={{ marginTop: 2, width: '300px' }}
        >
          Create
        </Button>
      </Box>
    </ThemeProvider>
  );
};

export default AddPersona;
