import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Grid, Box, Typography, Card, CardContent, CardMedia } from '@mui/material';

// Function to get a list of persons
const getPersonList = async (userId) => {
    const BASE_URL = 'https://peoplemuseumyeah.uc.r.appspot.com/db';

    try {
        const response = await axios.post(`${BASE_URL}/getPersonList`, {
            userId,
        }, {
            headers: {
                'Content-Type': 'application/json',
            },
        });
        console.log('Status:', response.status);
        console.log('Data:', response.data);
        return response;
    } catch (error) {
        console.error('Error:', error.message);
        if (error.response) {
            console.error('Status:', error.response.status);
            console.error('Data:', error.response.data);
        } else if (error.request) {
            console.error('No response received:', error.request);
        } else {
            console.error('Error setting up request:', error.message);
        }
    }
};

const PersonList = () => {
    const userId = '1'; // Hardcoded userId as a string
    const [people, setPeople] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPeople = async () => {
            try {
                const response = await getPersonList(userId);

                if (response && response.data && response.data.data) {
                    setPeople(response.data.data);
                } else {
                    console.log('No data returned');
                }
            } catch (error) {
                console.error('Error fetching people:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchPeople();
    }, []);

    if (loading) {
        return <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</Box>;
    }

    if (error) {
        return <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Error: {error}</Box>;
    }

    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: 2 }}>
            <Typography variant="h4" gutterBottom color="primary" sx={{ textAlign: 'center' }}>
                People List
            </Typography>
            <Grid container spacing={2} justifyContent="center">
                {people.length > 0 ? (
                    people.map((person, index) => (
                        <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
                            <Card>
                                <CardMedia
                                    component="img"
                                    height="200" // Increased the height of the image
                                    image={person.imageLink}
                                    alt={person.name}
                                    sx={{ objectFit: 'contain' }}
                                />
                                <CardContent>
                                    <Typography variant="h6" component="div" sx={{ textAlign: 'center' }}>
                                        {person.name}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center' }}>
                                        {person.description}
                                    </Typography>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))
                ) : (
                    <Typography variant="h6" color="text.secondary" sx={{ textAlign: 'center' }}>
                        No people found
                    </Typography>
                )}
            </Grid>
        </Box>
    );
};

export default PersonList;
