import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { TextField, Button, Box, Typography, Card, CardMedia } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// Import uploadImage function
import { uploadImage } from './uploadImage'; // Adjust the import path as per your file structure

// Theme definition remains the same
const theme = createTheme({
    palette: {
        background: {
            default: '#282c34',
        },
    },
    components: {
        MuiOutlinedInput: {
            styleOverrides: {
                root: {
                    '& .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    color: 'white',
                },
                input: {
                    color: 'white',
                },
                notchedOutline: {
                    borderColor: 'white',
                },
            },
        },
        MuiInputLabel: {
            styleOverrides: {
                root: {
                    color: 'white',
                },
            },
        },
    },
});

const UpdateCollection = () => {
    const { collectionId } = useParams();
    const [formData, setFormData] = useState({
        name: '',
        imageLink: '',
        description: '',
        pic: null,
        picURL: '',
    });
    const [imagePreview, setImagePreview] = useState(null);

    useEffect(() => {
        const fetchCollection = async () => {
            try {
                const response = await axios.post(
                    'https://peoplemuseumyeah.uc.r.appspot.com/db/getCollection',
                    { collectionId },
                    {
                        headers: {
                            'Content-Type': 'application/json',
                        },
                    }
                );
                if (response.data && response.data.data) {
                    const { name, imageLink, description } = response.data.data;
                    setFormData({
                        name,
                        imageLink,
                        description,
                        pic: null,
                        picURL: imageLink,
                    });
                    setImagePreview(imageLink);
                } else {
                    console.log('Collection not found');
                }
            } catch (error) {
                console.error('Error fetching collection:', error);
            }
        };

        fetchCollection();
    }, [collectionId]);

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setFormData({ ...formData, pic: file, picURL: '' });
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleURLChange = (e) => {
        const { value } = e.target;
        setFormData({ ...formData, picURL: value, pic: null });
        setImagePreview(value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            let imageUrl = formData.picURL || formData.imageLink;

            if (formData.pic) {
                imageUrl = await uploadImage(formData.pic); // Use uploadImage function here
            }

            const formDataToSend = {
                collectionId,
                newName: formData.name,
                newImageLink: imageUrl,
                newDescription: formData.description,
            };

            const response = await axios.put(
                'https://peoplemuseumyeah.uc.r.appspot.com/db/updateCollection',
                formDataToSend,
                {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                }
            );

            if (response.status === 200) {
                window.location.href = '/collection-list'; // Redirect after successful update
            } else {
                console.error('Update failed:', response.data.message);
            }
        } catch (error) {
            console.error('Error updating collection:', error);
        }
    };

    return (
        <ThemeProvider theme={theme}>
            <Box
                component="form"
                onSubmit={handleSubmit}
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    minHeight: '100vh',
                    p: 2,
                }}
            >
                <Typography variant="h4" gutterBottom color="white">
                    Update Collection
                </Typography>
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Name"
                    variant="outlined"
                    name="name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <Box sx={{ marginBottom: 2, width: '300px' }}>
                    <TextField
                        fullWidth
                        label="Image URL"
                        variant="outlined"
                        name="picURL"
                        value={formData.picURL}
                        onChange={handleURLChange}
                        InputLabelProps={{ style: { color: 'white' } }}
                    />
                    <Button variant="contained" component="label" sx={{ marginTop: 1 }}>
                        Upload Image
                        <input type="file" name="pic" hidden onChange={handleFileChange} />
                    </Button>
                </Box>
                {imagePreview && (
                    <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
                        <CardMedia
                            component="img"
                            height="auto"
                            image={imagePreview}
                            alt="Uploaded Image Preview"
                            sx={{ objectFit: 'contain' }}
                        />
                    </Card>
                )}
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Description"
                    variant="outlined"
                    name="description"
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                    multiline
                    rows={4}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    sx={{ marginTop: 2, width: '300px' }}
                >
                    Update
                </Button>
            </Box>
        </ThemeProvider>
    );
};

export default UpdateCollection;
