import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Box, Typography, TextField, Button } from '@mui/material';

const UpdateCollection = () => {
    const [collection, setCollection] = useState({
        collectionId: '5088723339313152', // Replace with actual collectionId
        name: '',
        imageLink: '',
        description: '',
        isPublic: false, // Assuming isPublic is a boolean field
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCollection = async () => {
            try {
                const response = await axios.get(`https://your-api-url.com/collections/${collection.collectionId}`);
                if (response.data) {
                    setCollection(response.data); // Update collection state with fetched data
                } else {
                    setError('Collection not found');
                }
            } catch (error) {
                console.error('Error fetching collection:', error);
                setError('Error fetching collection');
            }
        };

        fetchCollection();
    }, [collection.collectionId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCollection({ ...collection, [name]: value });
    };

    const handleUpdate = async () => {
        setLoading(true);

        try {
            const response = await axios.put(`https://your-api-url.com/updateCollection`, {
                collectionId: collection.collectionId,
                newName: collection.name,
                newImageLink: collection.imageLink,
                newDescription: collection.description,
                newIsPublic: collection.isPublic,
            });
            console.log('Update Response:', response.data);

            // Navigate to collections list page after update
            window.location.href = '/collections'; // Directly changing window location for navigation
        } catch (error) {
            console.error('Error updating collection:', error);
            setError('Error updating collection');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <Typography>Loading...</Typography>;
    }

    if (error) {
        return <Typography>Error: {error}</Typography>;
    }

    return (
        <Box sx={{ p: 2 }}>
            <Typography variant="h4" gutterBottom color="primary">
                Update Collection
            </Typography>
            <TextField
                fullWidth
                variant="outlined"
                label="Name"
                name="name"
                value={collection.name}
                onChange={handleChange}
                sx={{ marginBottom: 2 }}
            />
            <TextField
                fullWidth
                variant="outlined"
                label="Image Link"
                name="imageLink"
                value={collection.imageLink}
                onChange={handleChange}
                sx={{ marginBottom: 2 }}
            />
            <TextField
                fullWidth
                variant="outlined"
                label="Description"
                name="description"
                value={collection.description}
                onChange={handleChange}
                sx={{ marginBottom: 2 }}
            />
            <TextField
                fullWidth
                variant="outlined"
                label="Is Public"
                name="isPublic"
                type="checkbox"
                checked={collection.isPublic}
                onChange={(e) => setCollection({ ...collection, isPublic: e.target.checked })}
                sx={{ marginBottom: 2 }}
            />
            <Button onClick={handleUpdate} variant="contained" color="primary" sx={{ marginRight: 2 }}>
                Update Collection
            </Button>
            <Button component={Link} to="/collections" variant="outlined" color="secondary">
                Cancel
            </Button>
        </Box>
    );
};

export default UpdateCollection;
