import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getCollectionList } from '../../utils.js'; // 根据实际路径调整

const CollectionPage = () => {
  const { id } = useParams();
  const [collection, setCollection] = useState(null);

  useEffect(() => {
    const fetchCollectionsData = async () => {
      try {
        const response_getcollectionlist = await getCollectionList('1'); // 假设使用的userId为1
        if (response_getcollectionlist && response_getcollectionlist.data) {
          const collections = response_getcollectionlist.data.data;
          const foundCollection = collections.find(collection => collection.name === id);
          setCollection(foundCollection);
        }
      } catch (error) {
        console.error('Error fetching collections data:', error);
      }
    };

    fetchCollectionsData();
  }, [id]);

  if (!collection) {
    return <div>Collection not found</div>;
  }

  return (
    <div>
      <h1>{collection.name}</h1>
      <p>{collection.description}</p>
      <div>
        {collection.images && collection.images.map((image, index) => (
          <div key={index}>
            <img src={image.url} alt={image.description} />
            <p>{image.description}</p>
          </div>
        ))}
      </div>
      <Link to="/add-person-collection">
        <button type="button">Add Persona To Collection</button>
      </Link>
    </div>
  );
}

export default CollectionPage;
