import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Grid, Box, Typography, Card, CardContent, CardActions, Button, CardMedia } from '@mui/material';

const CollectionList = () => {
    const userId = '1'; // Hardcoded userId as a string for demonstration
    const [collections, setCollections] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCollections = async () => {
            try {
                const response = await getCollectionList(userId);

                if (response && response.data && response.data.data) {
                    setCollections(response.data.data);
                } else {
                    console.log('No data returned');
                }
            } catch (error) {
                console.error('Error fetching collections:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchCollections();
    }, []);

    const handleDelete = async (collectionId) => {
        try {
            await deleteCollection(collectionId);
            setCollections(collections.filter((collection) => collection.collectionId !== collectionId));
        } catch (error) {
            console.error('Error deleting collection:', error);
        }
    };

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                Loading...
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                Error: {error}
            </Box>
        );
    }

    return (
        <Box
            sx={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                minHeight: '100vh',
                p: 2,
            }}
        >
            <Typography variant="h4" gutterBottom color="primary" sx={{ textAlign: 'center' }}>
                Collection List
            </Typography>
            <Button
                component={Link}
                to="/add-collection"
                variant="contained"
                color="primary"
                sx={{ marginBottom: 2 }}
            >
                Add Collection
            </Button>
            <Grid container spacing={2} justifyContent="center">
                {collections.length > 0 ? (
                    collections.map((collection) => (
                        <Grid item key={collection.collectionId}>
                            <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
                                <CardMedia
                                    component="img"
                                    height="200"
                                    image={collection.imageLink} // Assuming imageLink is the URL to the image
                                    alt={collection.name}
                                    sx={{ objectFit: 'contain' }}
                                />
                                <CardContent>
                                    <Typography variant="h6" gutterBottom color="white">
                                        {collection.name}
                                    </Typography>
                                    <Typography variant="body2" color="white">
                                        Description: {collection.description}
                                    </Typography>
                                </CardContent>
                                <CardActions>
                                    <Button
                                        component={Link}
                                        to={`/update-collection/${collection.collectionId}`}
                                        variant="outlined"
                                        color="primary"
                                        sx={{ marginRight: 1 }}
                                    >
                                        Update
                                    </Button>
                                    <Button
                                        variant="outlined"
                                        color="secondary"
                                        onClick={() => handleDelete(collection.collectionId)}
                                    >
                                        Delete
                                    </Button>
                                </CardActions>
                            </Card>
                        </Grid>
                    ))
                ) : (
                    <Typography variant="h6" color="text.secondary" sx={{ textAlign: 'center' }}>
                        No collections found
                    </Typography>
                )}
            </Grid>
        </Box>
    );
};

export default CollectionList;
