import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useCollections } from '../../context/CollectionContext.js';
import { getPersonListByCollection } from '../../utils.js';
import { List, ListItem, ListItemAvatar, Avatar, ListItemText, Typography, Paper, Button } from '@mui/material';

const CollectionPage = () => {
  const { id } = useParams();
  const collections = useCollections();
  const collection = collections.find(collection => collection.id === parseInt(id));
  const [persons, setPersons] = useState([]);

  useEffect(() => {
    const fetchPersons = async () => {
      try {
        const response = await getPersonListByCollection(id);
        if (response && response.data) {
          setPersons(response.data.data);
        }
      } catch (error) {
        console.error('Error fetching persons data:', error);
      }
    };

    fetchPersons();
  }, [id]);

  if (!collection) {
    return <div>Collection not found</div>;
  }

  return (
    <div>
      <h1>{collection.name}</h1>
      <p>{collection.description}</p>
      <img src={collection.imageLink} alt={collection.name} />
      <Link to="/add-person-collection">
        <Button variant="contained" color="primary">Add Persona To Collection</Button>
      </Link>
      <Paper elevation={3} style={{ marginTop: '20px', padding: '20px' }}>
        <Typography variant="h6" gutterBottom>Persons in this Collection:</Typography>
        <List>
          {persons.map(person => (
            <ListItem key={person.userId}>
              <ListItemAvatar>
                <Avatar src={person.imageLink} alt={person.name} />
              </ListItemAvatar>
              <ListItemText
                primary={person.name}
                secondary={person.description}
              />
            </ListItem>
          ))}
        </List>
      </Paper>
    </div>
  );
}

export default CollectionPage;
