import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography, TextField, Button, Card, CardMedia } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        background: {
            default: '#282c34',
        },
    },
    components: {
        MuiOutlinedInput: {
            styleOverrides: {
                root: {
                    '& .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    color: 'white',
                },
                input: {
                    color: 'white',
                },
                notchedOutline: {
                    borderColor: 'white',
                },
            },
        },
        MuiInputLabel: {
            styleOverrides: {
                root: {
                    color: 'white',
                },
            },
        },
    },
});

const UpdatePerson = () => {
    const { personId } = useParams();
    const [person, setPerson] = useState({
        name: '',
        image: null,
        imageLink: '',
        description: '',
    });
    const [imagePreview, setImagePreview] = useState(null);

    useEffect(() => {
        const fetchPerson = async () => {
            try {
                const response = await axios.post(
                    'https://peoplemuseumyeah.uc.r.appspot.com/db/getPerson',
                    { personId },
                    {
                        headers: {
                            'Content-Type': 'application/json',
                        },
                    }
                );
                if (response.data && response.data.person) {
                    const { name, imageLink, description } = response.data.person;
                    setPerson({ name, imageLink, description });
                    setImagePreview(imageLink); // Set initial image preview
                } else {
                    console.log('Person not found');
                }
            } catch (error) {
                console.error('Error fetching person:', error);
            }
        };

        fetchPerson();
    }, [personId]);

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setPerson({ ...person, image: file, imageLink: '' });
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleURLChange = (e) => {
        const { value } = e.target;
        setPerson({ ...person, imageLink: value, image: null });
        setImagePreview(value);
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('personId', personId);
            formData.append('newName', person.name);
            formData.append('newDescription', person.description);
            formData.append('newContext', '');
            formData.append('newPublic', true);

            // Check if an image file is selected
            if (person.image) {
                formData.append('newImage', person.image);
            } else {
                // Use the existing image link if available
                formData.append('newImageLink', person.imageLink);
            }

            const response = await axios.put(
                'https://peoplemuseumyeah.uc.r.appspot.com/db/updatePerson',
                formData,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                }
            );
            // Navigate back to person list or homepage
            window.location.href = '/person-list'; // Change this route based on your application structure
        } catch (error) {
            console.error('Error updating person:', error);
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPerson({
            ...person,
            [name]: value,
        });
    };

    return (
        <ThemeProvider theme={theme}>
            <Box
                component="form"
                onSubmit={handleUpdate}
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    minHeight: '100vh',
                    p: 2,
                }}
            >
                <Typography variant="h4" gutterBottom color="white">
                    Update Person
                </Typography>
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Name"
                    variant="outlined"
                    name="name"
                    value={person.name}
                    onChange={handleChange}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <Box sx={{ marginBottom: 2, width: '300px' }}>
                    <TextField
                        fullWidth
                        label="Image URL"
                        variant="outlined"
                        name="imageLink"
                        value={person.imageLink}
                        onChange={handleURLChange}
                        InputLabelProps={{ style: { color: 'white' } }}
                    />
                    <Button
                        variant="contained"
                        component="label"
                        sx={{ marginTop: 1 }}
                    >
                        Upload Image
                        <input
                            type="file"
                            name="image"
                            hidden
                            onChange={handleFileChange}
                        />
                    </Button>
                </Box>
                {imagePreview && (
                    <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
                        <CardMedia
                            component="img"
                            height="auto"
                            image={imagePreview}
                            alt="Uploaded Image Preview"
                            sx={{ objectFit: 'contain' }}
                        />
                    </Card>
                )}
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Description"
                    variant="outlined"
                    name="description"
                    value={person.description}
                    onChange={handleChange}
                    multiline
                    rows={4}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    sx={{ marginTop: 2, width: '300px' }}
                >
                    Update
                </Button>
            </Box>
        </ThemeProvider>
    );
};

export default UpdatePerson;
