import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography, TextField, Button, Card, CardMedia } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    background: {
      default: '#282c34',
    },
  },
  components: {
    MuiOutlinedInput: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          '&:hover .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          color: 'white',
        },
        input: {
          color: 'white',
        },
        notchedOutline: {
          borderColor: 'white',
        },
      },
    },
    MuiInputLabel: {
      styleOverrides: {
        root: {
          color: 'white',
        },
      },
    },
  },
});

const UpdatePerson = () => {
  const { personId } = useParams();
  const [person, setPerson] = useState({
    name: '',
    imageLink: '',
    description: '',
  });

  useEffect(() => {
    const fetchPerson = async () => {
      try {
        const response = await axios.post('https://peoplemuseumyeah.uc.r.appspot.com/db/getPerson', {
          personId,
        }, {
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (response.data && response.data.person) {
          setPerson(response.data.person);
        } else {
          console.log('Person not found');
        }
      } catch (error) {
        console.error('Error fetching person:', error);
      }
    };

    fetchPerson();
  }, [personId]);

  const handleUpdate = async () => {
    try {
      await axios.put('https://peoplemuseumyeah.uc.r.appspot.com/db/updatePerson', {
        personId,
        newName: person.name,
        newImageLink: person.imageLink,
        newDescription: person.description,
        newContext: '',
        newPublic: true,
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      // Navigate back to person list or homepage
      window.location.href = '/person-list'; // Change this route based on your application structure
    } catch (error) {
      console.error('Error updating person:', error);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setPerson({
      ...person,
      [name]: value,
    });
  };

  return (
    <ThemeProvider theme={theme}>
      <Box
        component="form"
        onSubmit={handleUpdate}
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
          p: 2,
        }}
      >
        <Typography variant="h4" gutterBottom color="white">
          Update Person
        </Typography>
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Name"
          variant="outlined"
          name="name"
          value={person.name}
          onChange={handleChange}
          required
          InputLabelProps={{ style: { color: 'white' } }}
        />
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Image Link"
          variant="outlined"
          name="imageLink"
          value={person.imageLink}
          onChange={handleChange}
          InputLabelProps={{ style: { color: 'white' } }}
        />
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Description"
          variant="outlined"
          name="description"
          value={person.description}
          onChange={handleChange}
          multiline
          rows={4}
          required
          InputLabelProps={{ style: { color: 'white' } }}
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          sx={{ marginTop: 2, width: '300px' }}
        >
          Update
        </Button>
      </Box>
    </ThemeProvider>
  );
};

export default UpdatePerson;
