// src/components/CollectionPage/CollectionPage.jsx

import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { useCollections } from '../../context/CollectionContext.js';

const CollectionPage = () => {
  const { id } = useParams();
  const collections = useCollections();
  const collection = collections.find(collection => collection.id === id);

  if (!collection) {
    return <div>Collection not found</div>;
  }

  return (
    <div>
      <h1>{collection.name}</h1>
      <p>{collection.description}</p>
      <div>
        {collection.images && collection.images.map((image, index) => (
          <div key={index}>
            <img src={image.url} alt={image.description} />
            <p>{image.description}</p>
          </div>
        ))}
      </div>
      <Link to="/add-person-collection">
        <button type="button">Add Persona To Collection</button>
      </Link>
    </div>
  );
}

export default CollectionPage;
