import React from 'react';
import { Link } from 'react-router-dom';
import { Button, Grid, Typography } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  homepage: {
    padding: theme.spacing(2),
    textAlign: 'center',
  },
  photoWall: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
    gap: theme.spacing(2),
    padding: theme.spacing(2),
  },
  photo: {
    width: '100%',
    height: 'auto',
    borderRadius: theme.shape.borderRadius,
    boxShadow: theme.shadows[1],
    transition: 'transform 0.2s',
    '&:hover': {
      transform: 'scale(1.05)',
    },
  },
  buttons: {
    display: 'flex',
    justifyContent: 'center',
    gap: theme.spacing(2),
    marginTop: theme.spacing(4),
  },
}));

const HomePage = () => {
  const classes = useStyles();

  const people = [
    { id: 1, name: 'Person 1', photo:'https://images.unsplash.com/photo-1599566150163-29194dcaad36?crop=faces&fit=crop&w=200&h=200', description: 'Description of Person 1' },
    { id: 2, name: 'Person 2', photo:'https://images.unsplash.com/photo-1524504388940-b1c1722653e1?crop=faces&fit=crop&w=200&h=200', description: 'Description of Person 2' },
    { id: 3, name: 'Person 3', photo:'https://images.unsplash.com/photo-1494790108377-be9c29b29330?crop=faces&fit=crop&w=200&h=200', description: 'Description of Person 3' }
  ];

  return (
    <div className={classes.homepage}>
      <Typography variant="h1">People Museum</Typography>
      <div className={classes.photoWall}>
        {people.map(person => (
          <Link to={`/person/${person.id}`} key={person.id}>
            <img src={person.photo} alt={person.name} className={classes.photo} />
          </Link>
        ))}
      </div>
      <div className={classes.buttons}>
        <Link to="/add-persona">
          <Button variant="contained" color="primary">Add Persona</Button>
        </Link>
        <Link to="/add-collection">
          <Button variant="contained" color="primary">Add Collection</Button>
        </Link>
      </div>
    </div>
  );
}

export default HomePage;
