// src/context/CollectionContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import { getCollectionList } from '../utils';

const CollectionContext = createContext();

export const useCollections = () => {
  return useContext(CollectionContext);
};

export const CollectionProvider = ({ children }) => {
  const [collections, setCollections] = useState([]);

  const fetchCollections = async () => {
    try {
      const response = await getCollectionList('1');
      console.log('Fetched collections response:', response); // 添加日志
      if (response && response.data && Array.isArray(response.data.data)) {
        setCollections(response.data.data);
      } else {
        setCollections([]); // 确保 collections 始终是一个数组
      }
    } catch (error) {
      console.error('Error fetching collections:', error);
      setCollections([]); // 确保 collections 始终是一个数组
    }
  };

  useEffect(() => {
    fetchCollections();
  }, []);

  return (
    <CollectionContext.Provider value={{ collections, fetchCollections }}>
      {children}
    </CollectionContext.Provider>
  );
};
