import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import { Box, TextField, Button, Typography, Card, CardMedia } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        background: {
            default: '#282c34',
        },
    },
    components: {
        MuiOutlinedInput: {
            styleOverrides: {
                root: {
                    '& .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    color: 'white',
                },
                input: {
                    color: 'white',
                },
                notchedOutline: {
                    borderColor: 'white',
                },
            },
        },
        MuiInputLabel: {
            styleOverrides: {
                root: {
                    color: 'white',
                },
            },
        },
    },
});

const UpdatePerson = () => {
    const { personId } = useParams();
    const [formData, setFormData] = useState({
        newName: '',
        newImageLink: '',
        newDescription: '',
        newContext: '',
        newPublic: false,
    });
    const [imagePreview, setImagePreview] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPerson = async () => {
            try {
                const response = await axios.post('/getPerson', { personId });
                if (response.data && response.data.person) {
                    const person = response.data.person;
                    setFormData({
                        newName: person.name,
                        newImageLink: person.imageLink,
                        newDescription: person.description,
                        newContext: person.context,
                        newPublic: person.public,
                    });
                    setImagePreview(person.imageLink);
                } else {
                    console.log('No data returned');
                }
            } catch (error) {
                console.error('Error fetching person:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchPerson();
    }, [personId]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData({ ...formData, [name]: type === 'checkbox' ? checked : value });

        if (name === 'newImageLink') {
            setImagePreview(value);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.put('/updatePerson', {
                personId,
                ...formData,
            });

            console.log('Person updated successfully:', response.data);

            // Redirect or navigate to another page, for example, back to the list
            // You can use Link for declarative navigation in other parts of the app
        } catch (error) {
            console.error('Error updating person:', error);
        }
    };

    if (loading) {
        return <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</Box>;
    }

    if (error) {
        return <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Error: {error}</Box>;
    }

    return (
        <ThemeProvider theme={theme}>
            <Box
                component="form"
                onSubmit={handleSubmit}
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    minHeight: '100vh',
                    p: 2,
                }}
            >
                <Typography variant="h4" gutterBottom color="white">
                    Update Person
                </Typography>
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Name"
                    variant="outlined"
                    name="newName"
                    value={formData.newName}
                    onChange={handleChange}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Image Link"
                    variant="outlined"
                    name="newImageLink"
                    value={formData.newImageLink}
                    onChange={handleChange}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Description"
                    variant="outlined"
                    name="newDescription"
                    value={formData.newDescription}
                    onChange={handleChange}
                    multiline
                    rows={4}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Context"
                    variant="outlined"
                    name="newContext"
                    value={formData.newContext}
                    onChange={handleChange}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <Box sx={{ marginBottom: 2 }}>
                    <Typography variant="body1" color="white">Public:</Typography>
                    <input
                        type="checkbox"
                        name="newPublic"
                        checked={formData.newPublic}
                        onChange={handleChange}
                    />
                </Box>
                {imagePreview && (
                    <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
                        <CardMedia
                            component="img"
                            height="auto"
                            image={imagePreview}
                            alt="Image Preview"
                            sx={{ objectFit: 'contain' }}
                        />
                    </Card>
                )}
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    sx={{ marginTop: 2, width: '300px' }}
                >
                    Update
                </Button>
                <Link to="/">Back to List</Link>
            </Box>
        </ThemeProvider>
    );
};

export default UpdatePerson;
