import React, { useState } from 'react';
import axios from 'axios'; // Import axios for making HTTP requests
import { TextField, Button, Box, Typography, Card, CardMedia } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    background: {
      default: '#282c34',
    },
  },
  components: {
    MuiOutlinedInput: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          '&:hover .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          color: 'white',
        },
        input: {
          color: 'white',
        },
        notchedOutline: {
          borderColor: 'white',
        },
      },
    },
    MuiInputLabel: {
      styleOverrides: {
        root: {
          color: 'white',
        },
      },
    },
  },
});

const AddPersona = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    description: '',
    pic: null,
    picURL: '',
  });
  const [imagePreview, setImagePreview] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFormData({ ...formData, pic: file, picURL: '' });
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleURLChange = (e) => {
    const { value } = e.target;
    setFormData({ ...formData, picURL: value, pic: null });
    setImagePreview(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('firstName', formData.firstName);
      formData.append('lastName', formData.lastName);
      formData.append('description', formData.description);
      formData.append('pic', formData.pic);

      const response = await fetch('http://127.0.0.1:8080/addPerson', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      console.log('Person added successfully:', data);
      // Optionally reset form fields or show a success message
    } catch (error) {
      console.error('Error adding person:', error);
      // Handle error states (e.g., show error message to user)
    }
  };





  return (
    <ThemeProvider theme={theme}>
      <Box
        component="form"
        onSubmit={handleSubmit}
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
          p: 2,
        }}
      >
        <Typography variant="h4" gutterBottom color="white">
          Add Person to Museum
        </Typography>
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="First Name"
          variant="outlined"
          name="firstName"
          value={formData.firstName}
          onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
          required
          InputLabelProps={{ style: { color: 'white' } }}
        />
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Last Name"
          variant="outlined"
          name="lastName"
          value={formData.lastName}
          onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
          required
          InputLabelProps={{ style: { color: 'white' } }}
        />
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Description"
          variant="outlined"
          name="description"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          multiline
          rows={4}
          required
          InputLabelProps={{ style: { color: 'white' } }}
        />
        <Box sx={{ marginBottom: 2, width: '300px' }}>
          <TextField
            fullWidth
            label="Image URL"
            variant="outlined"
            name="picURL"
            value={formData.picURL}
            onChange={handleURLChange}
            InputLabelProps={{ style: { color: 'white' } }}
          />
          <Button
            variant="contained"
            component="label"
            sx={{ marginTop: 1 }}
          >
            Upload Image
            <input
              type="file"
              name="pic"
              hidden
              onChange={handleFileChange}
            />
          </Button>
        </Box>
        {imagePreview && (
          <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
            <CardMedia
              component="img"
              height="auto"
              image={imagePreview}
              alt="Uploaded Image Preview"
              sx={{ objectFit: 'contain' }}
            />
          </Card>
        )}
        <Button
          type="submit"
          variant="contained"
          color="primary"
          sx={{ marginTop: 2, width: '300px' }}
        >
          Create
        </Button>
      </Box>
    </ThemeProvider>
  );
};

export default AddPersona;
