import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography, TextField, Button } from '@mui/material';

const UpdatePerson = () => {
    const { personId } = useParams();
    const [person, setPerson] = useState({
        name: '',
        imageLink: '',
        description: '',
    });

    useEffect(() => {
        const fetchPerson = async () => {
            try {
                const response = await axios.post('https://peoplemuseumyeah.uc.r.appspot.com/db/getPerson', {
                    personId,
                }, {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                if (response.data && response.data.person) {
                    setPerson(response.data.person);
                } else {
                    console.log('Person not found');
                }
            } catch (error) {
                console.error('Error fetching person:', error);
            }
        };

        fetchPerson();
    }, [personId]);

    const handleUpdate = async () => {
        try {
            await axios.put('https://peoplemuseumyeah.uc.r.appspot.com/db/updatePerson', {
                personId,
                newName: person.name,
                newImageLink: person.imageLink,
                newDescription: person.description,
                newContext: '',
                newPublic: true,
            }, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            // Navigate back to person list
            window.location.href = '/person-list';
        } catch (error) {
            console.error('Error updating person:', error);
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPerson({
            ...person,
            [name]: value,
        });
    };

    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: 2 }}>
            <Typography variant="h4" gutterBottom color="primary" sx={{ textAlign: 'center' }}>
                Update Person
            </Typography>
            <Box sx={{ width: '100%', maxWidth: 400 }}>
                <TextField
                    fullWidth
                    label="Name"
                    name="name"
                    value={person.name}
                    onChange={handleChange}
                    sx={{ marginBottom: 2 }}
                />
                <TextField
                    fullWidth
                    label="Image Link"
                    name="imageLink"
                    value={person.imageLink}
                    onChange={handleChange}
                    sx={{ marginBottom: 2 }}
                />
                <TextField
                    fullWidth
                    label="Description"
                    name="description"
                    multiline
                    minRows={4}
                    value={person.description}
                    onChange={handleChange}
                    sx={{ marginBottom: 2 }}
                />
                <Box sx={{ display: 'flex', justifyContent: 'center', marginTop: 2 }}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleUpdate}
                        sx={{ marginRight: 1 }}
                    >
                        Update
                    </Button>
                    <Button
                        component={Link}
                        to="/person-list"
                        variant="outlined"
                        color="secondary"
                    >
                        Cancel
                    </Button>
                </Box>
            </Box>
        </Box>
    );
};

export default UpdatePerson;
