// PersonDetails.js
import React from 'react';
import { Box, Typography } from '@mui/material';

const PersonDetails = ({ photo, name, description }) => (
    <Box component="header" sx={{ textAlign: 'center', mb: 3 }}>
        <img src={photo} alt={name} style={{ maxWidth: 300, height: 'auto', borderRadius: 8 }} />
        <Typography variant="h4" component="h2" sx={{ mt: 4, mb: 2 }}>
            {name}
        </Typography>
        <Typography variant="body1" sx={{ mt: 2 }}>
            {description}
        </Typography>
    </Box>
);

export default PersonDetails;
