import React, { createContext, useState, useEffect, useContext } from 'react';
import { getCollectionList } from '../../utils.js';

const CollectionContext = createContext();

export const useCollections = () => {
  return useContext(CollectionContext);
};

export const CollectionProvider = ({ children }) => {
  const [collections, setCollections] = useState([]);

  useEffect(() => {
    const fetchCollectionsData = async () => {
      try {
        const response_getcollectionlist = await getCollectionList('1');
        if (response_getcollectionlist && response_getcollectionlist.data) {
          setCollections(response_getcollectionlist.data.data);
        }
      } catch (error) {
        console.error('Error fetching collections data:', error);
      }
    };

    fetchCollectionsData();
  }, []);

  return (
    <CollectionContext.Provider value={collections}>
      {children}
    </CollectionContext.Provider>
  );
};
