import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography, TextField, Button } from '@mui/material';

const UpdateCollectionPage = () => {
    const { collectionId } = useParams(); // Fetch collectionId from URL params
    const history = useHistory();

    const [collection, setCollection] = useState({
        name: '',
        imageLink: '',
        description: '',
        isPublic: false, // Assuming isPublic is a boolean field
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCollection = async () => {
            try {
                const response = await axios.get(`https://your-api-url.com/collections/${collectionId}`);
                if (response.data) {
                    setCollection(response.data); // Update collection state with fetched data
                } else {
                    setError('Collection not found');
                }
            } catch (error) {
                console.error('Error fetching collection:', error);
                setError('Error fetching collection');
            }
        };

        fetchCollection();
    }, [collectionId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCollection({ ...collection, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const response = await axios.put(`https://your-api-url.com/updateCollection`, {
                collectionId,
                newName: collection.name,
                newImageLink: collection.imageLink,
                newDescription: collection.description,
                newIsPublic: collection.isPublic,
            });
            console.log('Update Response:', response.data);
            // Optionally handle success message or navigate to another page
            history.push('/collections'); // Redirect to collections list page after update
        } catch (error) {
            console.error('Error updating collection:', error);
            setError('Error updating collection');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <Typography>Loading...</Typography>;
    }

    if (error) {
        return <Typography>Error: {error}</Typography>;
    }

    return (
        <Box sx={{ p: 2 }}>
            <Typography variant="h4" gutterBottom color="primary">
                Update Collection
            </Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    fullWidth
                    variant="outlined"
                    label="Name"
                    name="name"
                    value={collection.name}
                    onChange={handleChange}
                    sx={{ marginBottom: 2 }}
                />
                <TextField
                    fullWidth
                    variant="outlined"
                    label="Image Link"
                    name="imageLink"
                    value={collection.imageLink}
                    onChange={handleChange}
                    sx={{ marginBottom: 2 }}
                />
                <TextField
                    fullWidth
                    variant="outlined"
                    label="Description"
                    name="description"
                    value={collection.description}
                    onChange={handleChange}
                    sx={{ marginBottom: 2 }}
                />
                <TextField
                    fullWidth
                    variant="outlined"
                    label="Is Public"
                    name="isPublic"
                    type="checkbox"
                    checked={collection.isPublic}
                    onChange={(e) => setCollection({ ...collection, isPublic: e.target.checked })}
                    sx={{ marginBottom: 2 }}
                />
                <Button type="submit" variant="contained" color="primary">
                    Update Collection
                </Button>
            </form>
        </Box>
    );
};

export default UpdateCollectionPage;
