import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { TextField, Button, Box, Typography, Card, CardMedia } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    background: {
      default: '#282c34',
    },
  },
  components: {
    MuiOutlinedInput: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          '&:hover .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
            borderColor: 'white',
          },
          color: 'white',
        },
        input: {
          color: 'white',
        },
        notchedOutline: {
          borderColor: 'white',
        },
      },
    },
    MuiInputLabel: {
      styleOverrides: {
        root: {
          color: 'white',
        },
      },
    },
  },
});

const UpdatePerson = () => {
  const { personId } = useParams();
  const [person, setPerson] = useState({
    name: '',
    imageLink: '',
    description: '',
  });
  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPerson = async () => {
      try {
        const response = await axios.post('https://peoplemuseumyeah.uc.r.appspot.com/db/getPerson', {
          personId,
        }, {
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (response.data && response.data.person) {
          const { name, imageLink, description } = response.data.person;
          setPerson({ name, imageLink, description });
          setImagePreview(imageLink); // Set initial image preview
        } else {
          console.log('Person not found');
        }
      } catch (error) {
        console.error('Error fetching person:', error);
        setError('Error fetching person data.');
      } finally {
        setLoading(false);
      }
    };

    fetchPerson();
  }, [personId]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setPerson({ ...person, imageLink: '', pic: file });
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleURLChange = (e) => {
    const { value } = e.target;
    setPerson({ ...person, imageLink: value, pic: null });
    setImagePreview(value);
  };

  const uploadImage = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('https://your-upload-api-endpoint.com/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      return response.data.imageUrl; // Assuming the response contains the uploaded image URL
    } catch (error) {
      console.error('Error uploading image:', error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      let imageUrl = person.imageLink;

      if (person.pic) {
        imageUrl = await uploadImage(person.pic);
      }

      const formDataToSend = {
        personId,
        newName: person.name,
        newImageLink: imageUrl,
        newDescription: person.description,
        newContext: '',
        newPublic: true,
      };

      const response = await axios.put('https://peoplemuseumyeah.uc.r.appspot.com/db/updatePerson', formDataToSend, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 200) {
        window.location.href = '/person-list'; // Change this route based on your application structure
      } else {
        console.error('Update failed:', response.data.message);
      }
    } catch (error) {
      console.error('Error updating person:', error);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setPerson({
      ...person,
      [name]: value,
    });
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography variant="h5">Loading...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography variant="h5" color="error">{error}</Typography>
      </Box>
    );
  }

  return (
    <ThemeProvider theme={theme}>
      <Box
        component="form"
        onSubmit={handleSubmit}
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
          p: 2,
        }}
      >
        <Typography variant="h4" gutterBottom color="white">
          Update Person
        </Typography>
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Name"
          variant="outlined"
          name="name"
          value={person.name}
          onChange={handleChange}
          required
          InputLabelProps={{ style: { color: 'white' } }}
        />
        <Box sx={{ marginBottom: 2, width: '300px' }}>
          <TextField
            fullWidth
            label="Image URL"
            variant="outlined"
            name="imageLink"
            value={person.imageLink}
            onChange={handleURLChange}
            InputLabelProps={{ style: { color: 'white' } }}
          />
          <Button
            variant="contained"
            component="label"
            sx={{ marginTop: 1 }}
          >
            Upload Image
            <input
              type="file"
              name="pic"
              hidden
              onChange={handleFileChange}
            />
          </Button>
        </Box>
        {imagePreview && (
          <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
            <CardMedia
              component="img"
              height="auto"
              image={imagePreview}
              alt="Uploaded Image Preview"
              sx={{ objectFit: 'contain' }}
            />
          </Card>
        )}
        <TextField
          sx={{ marginBottom: 2, width: '300px' }}
          label="Description"
          variant="outlined"
          name="description"
          value={person.description}
          onChange={handleChange}
          multiline
          rows={4}
          required
          InputLabelProps={{ style: { color: 'white' } }}
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          sx={{ marginTop: 2, width: '300px' }}
        >
          Update
        </Button>
      </Box>
    </ThemeProvider>
  );
};

export default UpdatePerson;
