import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography, TextField, Button, Card, CardMedia } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        background: {
            default: '#282c34',
        },
    },
    components: {
        MuiOutlinedInput: {
            styleOverrides: {
                root: {
                    '& .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'white',
                    },
                    color: 'white',
                },
                input: {
                    color: 'white',
                },
                notchedOutline: {
                    borderColor: 'white',
                },
            },
        },
        MuiInputLabel: {
            styleOverrides: {
                root: {
                    color: 'white',
                },
            },
        },
    },
});

const UpdateCollection = () => {
    const { collectionId } = useParams();
    const [collection, setCollection] = useState({
        name: '',
        imageLink: '',
        description: '',
    });
    const [imagePreview, setImagePreview] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCollection = async () => {
            try {
                const response = await axios.post('https://peoplemuseumyeah.uc.r.appspot.com/db/getCollection', {
                    collectionId,
                }, {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                if (response.data && response.data.collection) {
                    const { name, imageLink, description } = response.data.collection;
                    setCollection({ name, imageLink, description });
                    setImagePreview(imageLink); // Set initial image preview
                } else {
                    console.log('Collection not found');
                }
            } catch (error) {
                console.error('Error fetching collection:', error);
                setError('Error fetching collection data.');
            } finally {
                setLoading(false);
            }
        };

        fetchCollection();
    }, [collectionId]);

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setCollection({ ...collection, imageLink: '', pic: file });
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleURLChange = (e) => {
        const { value } = e.target;
        setCollection({ ...collection, imageLink: value, pic: null });
        setImagePreview(value);
    };

    const uploadImage = async (file) => {
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('https://your-upload-api-endpoint.com/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            return response.data.imageUrl; // Assuming the response contains the uploaded image URL
        } catch (error) {
            console.error('Error uploading image:', error);
            throw error;
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            let imageUrl = collection.imageLink;

            if (collection.pic) {
                imageUrl = await uploadImage(collection.pic);
            }

            const formDataToSend = {
                collectionId,
                newName: collection.name,
                newImageLink: imageUrl,
                newDescription: collection.description,
                newIsPublic: true,
            };

            const response = await axios.put('https://peoplemuseumyeah.uc.r.appspot.com/db/updateCollection', formDataToSend, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (response.status === 200) {
                window.location.href = '/collections'; // Change this route based on your application structure
            } else {
                console.error('Update failed:', response.data.message);
            }
        } catch (error) {
            console.error('Error updating collection:', error);
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setCollection({
            ...collection,
            [name]: value,
        });
    };

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                <Typography variant="h5">Loading...</Typography>
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                <Typography variant="h5" color="error">{error}</Typography>
            </Box>
        );
    }

    return (
        <ThemeProvider theme={theme}>
            <Box
                component="form"
                onSubmit={handleSubmit}
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    minHeight: '100vh',
                    p: 2,
                }}
            >
                <Typography variant="h4" gutterBottom color="white">
                    Update Collection
                </Typography>
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Name"
                    variant="outlined"
                    name="name"
                    value={collection.name}
                    onChange={handleChange}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <Box sx={{ marginBottom: 2, width: '300px' }}>
                    <TextField
                        fullWidth
                        label="Image URL"
                        variant="outlined"
                        name="imageLink"
                        value={collection.imageLink}
                        onChange={handleURLChange}
                        InputLabelProps={{ style: { color: 'white' } }}
                    />
                    <Button
                        variant="contained"
                        component="label"
                        sx={{ marginTop: 1 }}
                    >
                        Upload Image
                        <input
                            type="file"
                            name="pic"
                            hidden
                            onChange={handleFileChange}
                        />
                    </Button>
                </Box>
                {imagePreview && (
                    <Card sx={{ maxWidth: 300, marginBottom: 2 }}>
                        <CardMedia
                            component="img"
                            height="auto"
                            image={imagePreview}
                            alt="Uploaded Image Preview"
                            sx={{ objectFit: 'contain' }}
                        />
                    </Card>
                )}
                <TextField
                    sx={{ marginBottom: 2, width: '300px' }}
                    label="Description"
                    variant="outlined"
                    name="description"
                    value={collection.description}
                    onChange={handleChange}
                    multiline
                    rows={4}
                    required
                    InputLabelProps={{ style: { color: 'white' } }}
                />
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    sx={{ marginTop: 2, width: '300px' }}
                >
                    Update
                </Button>
                <Button
                    component={Link}
                    to="/collections"
                    variant="outlined"
                    color="secondary"
                    sx={{ marginTop: 2, width: '300px' }}
                >
                    Cancel
                </Button>
            </Box>
        </ThemeProvider>
    );
};

export default UpdateCollection;
